import React from 'react';
import { NavLink } from 'react-router-dom';
import { useCart } from './Cart'; // Import the useCart hook

const Productdata = ({ image, title, des, price, color, size }) => {
  const { addToCart } = useCart(); // Access the addToCart function from the cart context

  return (
    <div className="product-item">
      <div className="product-image">
        <img src={image} alt="Product" />
      </div>
      <div className="product-details">
        <h2>{title}</h2>
        <p>{des}</p>
        <div className="product-actions">
          <p>Price: ${price}</p>
          {/* Add to Cart button */}
          <button className="cart-button" onClick={() => addToCart({ title, price })}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default Productdata;
