// ProductDetail.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Data from './Data';
import './productDetailStyles.css'; // Import the CSS file
 // Import useCart hook

const ProductDetail = () => {
  const { title } = useParams();
  const { addToCart } = useCart(); // Get the addToCart function from the CartContext

  // Find the product from the Data array based on the title parameter
  const product = Data.find(product => product.title === title);

  // State to keep track of selected color
  const [selectedColor, setSelectedColor] = useState(product.color[0]);

  const handleColorChange = (color) => {
    setSelectedColor(color);
  };

  const handleAddToCart = () => {
    addToCart({ ...product, color: selectedColor }); // Add the selected product with color to the cart
    alert('Product added to cart!');
  };

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="product-detail">
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} />
      <p>{product.des}</p>
      <p className="price">Price: ${product.price}</p>
      <div className="color-options">
        <p>Available Colors:</p>
        <div className="color-buttons">
          {product.color.map((color, index) => (
            <button
              key={index}
              className={`color-button ${selectedColor === color ? 'active' : ''}`}
              style={{ backgroundColor: color }}
              onClick={() => handleColorChange(color)}
            ></button>
          ))}
        </div>
      </div>
      <button className="add-to-cart" onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
};

export default ProductDetail;
