// Cart.js
import React, { useContext, useEffect } from 'react';
import { CartContext } from './CartContext';
import './cartStyles.css'; // Ensure this import is correct

const Cart = () => {
  const { cartItems, setCartItems, resetCartView, updateCartItemQuantity } = useContext(CartContext);

  useEffect(() => {
    resetCartView();
  }, [resetCartView]);

  const handleRemoveItem = (index) => {
    setCartItems((prevItems) => prevItems.filter((_, i) => i !== index));
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index} className="cart-item">
              <img src={item.image} alt={item.title} className="cart-item-image" />
              <div className="cart-item-details">
                <span className="cart-item-title">{item.title}</span>
                <span className="cart-item-price">${item.price.toFixed(2)}</span>
                <span className="cart-item-quantity">Quantity: {item.quantity}</span>
              </div>
              <div className="cart-item-actions">
                <button className="quantity-button" onClick={() => updateCartItemQuantity(index, 1)}>+1</button>
                <button className="quantity-button" onClick={() => updateCartItemQuantity(index, -1)}>-1</button>
                <button className="remove-button" onClick={() => handleRemoveItem(index)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      )}
      {cartItems.length > 0 && (
        <>
          <div className="total-price">
            Total: ${getTotalPrice().toFixed(2)}
          </div>
          <button className="checkout-button">Checkout</button>
        </>
      )}
    </div>
  );
};

export default Cart;
