import React, { useRef, useEffect } from 'react';
import './Slider.css'; // Import CSS file for styling

const Slider = () => {
  const sliderRef = useRef(null);

  useEffect(() => {
    const slider = sliderRef.current;

    // Function to automatically scroll the slider
    const scrollSlider = () => {
      slider.scrollLeft += 2; // Adjust scrolling speed as needed
    };

    // Start scrolling the slider automatically
    const intervalId = setInterval(scrollSlider, 50); // Adjust scroll interval as needed

    // Clean up function to stop scrolling when component unmounts
    return () => {
      clearInterval(intervalId);
    };
  }, []);

  return (
    <div className="slider" ref={sliderRef}>
      <div className="slide">Slide 1</div>
      <div className="slide">Slide 2</div>
      <div className="slide">Slide 3</div>
      {/* Add more slides as needed */}
    </div>
  );
};

export default Slider;
