import React from 'react';
import { useParams, useHistory } from 'react-router-dom'; // Import useHistory
import Data from './Data';
import './productDetailStyles.css'; 

const ProductDetail = () => {
  const { title } = useParams();
  const history = useHistory(); // Initialize useHistory

  const product = Data.find(product => product.title === title);

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleAddToCart = () => {
    console.log(`Added ${product.title} to cart`);
  };

  const handleSelectColor = (color) => {
    console.log(`Selected color: ${color}`);
  };

  const navigateToHome = () => {
    history.push('/'); // Navigate to the home page
  };

  return (
    <div className="product-detail">
      <div className="product-image">
        <img src={product.image} alt={product.title} className='prodct-detail-img'/>
      </div>
      <div className="product-details">
        <h2>{product.title}</h2>
        <p>{product.des}</p>
        <div className="color-selection">
          <p>Available Colors:</p>
          {product.color.map((color, index) => (
            <button
              key={index}
              className="color-button"
              style={{ backgroundColor: color }}
              onClick={() => handleSelectColor(color)}
            ></button>
          ))}
        </div>
        <div className="size-section">
          <p>Available Sizes:</p>
          <ul>
            {product.size.map((size, index) => (
              <li key={index}>{size}</li>
            ))}
          </ul>
        </div>
        <p className="price">Price: ${product.price}</p>
        <button className="cart-button" onClick={handleAddToCart}>Add to Cart</button>
        <button className="home-button" onClick={navigateToHome}>Go to Home</button> {/* New button for navigation */}
      </div>
    </div>
  );
};

export default ProductDetail;
