import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./simpleSlider.css";
import assets from "../assets";


function Arrow(props) {
  const { className, style, onClick } = props;

  const handleClick = () => {
    if (onClick) {
      onClick();
    }
  };

  return (
    <div
      className={className}
      style={{
        ...style,
        display: "block",
      }}
      onClick={handleClick}
    />
  );
}

function SimpleSlider() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <Arrow />,
    prevArrow: <Arrow />,
  };

  const images = [
    { id: 1, src: assets.image1, alt: "Image 1" },
    { id: 2, src: assets.image2, alt: "Image 2" },
    { id: 3, src: assets.image3, alt: "Image 3" },
    { id: 4, src: assets.image4, alt: "Image 4" },
    { id: 5, src: assets.image5, alt: "Image 5" },
    { id: 6, src: assets.image6, alt: "Image 6" },
  ];

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {images.map((image) => (
          <div key={image.id}>
            <div className="box">
              <img src={image.src} alt={image.alt} />
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default SimpleSlider;
