// ProductPage.js
import React from "react";
import { useParams } from "react-router-dom";
import Data from "./Data"; // Import the product data array

const ProductPage = () => {
  const { title } = useParams();

  // Find the product with the matching title
  const product = Data.find((product) => product.title === title);

  return (
    <div>
      <h2>Product Details</h2>
      {product ? (
        <div>
          <img src={product.image} alt="Product" />
          <h3>{product.title}</h3>
          <p>{product.des}</p>
          <p>Price: ${product.price}</p>
          <p>Available Colors: {product.color.join(", ")}</p>
          <p>Available Sizes: {product.size.join(", ")}</p>
        </div>
      ) : (
        <p>Product not found</p>
      )}
    </div>
  );
};

export default ProductPage;
