import React from 'react';
import { useParams } from 'react-router-dom';
import Data from './data';
import './productDetailStyles.css';

const ProductDetails = () => {
  const { id } = useParams();
  const product = Data.find(p => p.id === parseInt(id));

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleSelectColor = (color) => {
    console.log(`Selected color: ${color}`);
  };

  const handleAddToCart = () => {
    console.log(`Added ${product.title} to cart`);
  };

  return (
    <div className="product-details-page">
      <img src={product.image} alt={product.title} className="product-images" />
      <div className="product-detail">
        <h1>{product.title}</h1>
        <p className="product-price">Price: ₹{product.price}</p>
        <div className="available-colors">
        <p>Available Colors:</p>
        {product.color.map((color, index) => (
          <button
            key={index}
            className="color-button"
            style={{ backgroundColor: color }}
            onClick={() => handleSelectColor(color)}></button>
        ))}
        </div>
        <div className="size-section">
          <p>Available Sizes:</p>
          <ul>
            {product.size.map((size, index) => (
              <li key={index}>{size}</li>
            ))}
          </ul>
        </div>
        <button className="cart-button" onClick={handleAddToCart}>Add to Cart</button>
        <div className="extra-text">
          <p>Originally designed for performance hoops, the durable construction, tough stitching and easy-entry lacing deliver comfort that lasts. Vulcanised construction fuses the sole to the upper for a flexible, broken-in feel.</p>
        </div>
      </div>
      <footer className="footer">
        <p>© 2024 NIKE. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default ProductDetails;
