import React, { useState } from "react";

function ToDoList() {
 const [task,setTask] = useState(["eat breakfast" , "take a shower ", "walk a dog"]);
 const [newTask,setNewTask] = useState("");

 function handleInputChange(event){
setNewTask(event.target.value);
 }
function addtask(){
  if(newTask.trim()!=="")
  setTask(t =>[...task,newTask]);
  setNewTask("");
}
function deleteTask(index){
 const updatetask = task.filter((_,i) => i !== index);
 setTask(updatetask);

}
function moveTaskUp(index){
if(index>0){
  const updatetask = [...task];
  [updatetask[index],updatetask[index-1]]=
  [updatetask[index-1],updatetask[index]];
  setTask(updatetask);
}
}
function moveTaskdown(index){
  if(index<task.length-1){
    const updatetask = [...task];
    [updatetask[index],updatetask[index + 1]]=
    [updatetask[index-1],updatetask[index]];
    setTask(updatetask);
  }

}
return (
  <div className="to-do-list">
    <h1>To-Do List</h1>
    <div>
      <input type="text" placeholder="Enter a task.." value={newTask} onChange={handleInputChange} />
      <button className="add-button" onClick={addtask}>Add</button>
      <ol>
        {task.map((element, index) => (
          <li key={index}>
            <span className="text">{element}</span>
            <button className="delete-button" onClick={() => deleteTask(index)}>Delete</button>
            <button className="move-but" onClick={() => moveTaskUp(index)}>Up</button>
            <button className="down-but" onClick={() => moveTaskdown(index)}>Down</button>
          </li>
        ))}
      </ol>
    </div>
  </div>
);
}

export default ToDoList;
