import { CartContext } from './CartContext';
import './cartStyles.css'; // Ensure this import is correct

const Cart = () => {
  const { cartItems, resetCartView } = useContext(CartContext);

  useEffect(() => {
    resetCartView();
  }, [resetCartView]);

  const handleRemoveItem = (index) => {
    // Implement logic to remove item from cart
  };

  const getTotalPrice = () => {
    // Implement logic to calculate total price
  };

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              {item.title} - ${item.price}
              <button onClick={() => handleRemoveItem(index)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
      {cartItems.length > 0 && (
        <div>
          Total: ${getTotalPrice()}
          <button>Checkout</button>
        </div>
      )}
    </div>
  );
};

export default Cart;
