// CartContext.js
import React, { createContext, useState, useEffect } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState(() => {
    const savedCart = localStorage.getItem('cartItems');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  const [hasViewedCart, setHasViewedCart] = useState(false);

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  const addToCart = (product) => {
    setCartItems((prevItems) => [...prevItems, product]);
    setHasViewedCart(false); // Reset view status when a product is added
  };

  const resetCartView = () => {
    setHasViewedCart(true);
  };

  return (
    <CartContext.Provider value={{ cartItems, setCartItems, addToCart, hasViewedCart, resetCartView }}>
      {children}
    </CartContext.Provider>
  );
};
