// Product.js
import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import "./productstyles.css";
import Productdata from "./productdata";
import Data from "./Data";
import ProductPage from "./ProductPage"; // Import the ProductPage component

function Product() {
  return (
    <Router>
      <div className="product">
        <h1>Products</h1>
        <Switch>
          <Route path="/" exact>
            <div className="product-list">
              {Data.map((product) => (
                <Productdata
                  key={product.id}
                  image={product.image}
                  title={product.title}
                  des={product.des}
                  price={product.price}
                  color={product.color}
                  size={product.size}
                />
              ))}
            </div>
          </Route>
          {/* Route to the ProductPage component */}
          <Route path="/product/:title" component={ProductPage} />
        </Switch>
      </div>
    </Router>
  );
}

export default Product;
