import React, { useRef } from "react";
import Slider from "react-slick";
import "./simpleSlider.css"; // Import CSS for styling

export default function SimpleSlider() {
  const sliderRef = useRef(null);

  const slideLeft = () => {
    sliderRef.current.slickPrev();
  };

  const slideRight = () => {
    sliderRef.current.slickNext();
  };

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div className="slider-container">
      <Slider {...settings} ref={sliderRef}>
        <div className="slide-box">
          <div className="box">
            <p>Box 1</p>
          </div>
        </div>
        <div className="slide-box">
          <div className="box">
            <p>Box 2</p>
          </div>
        </div>
        <div className="slide-box">
          <div className="box">
            <p>Box 3</p>
          </div>
        </div>
        <div className="slide-box">
          <div className="box">
            <p>Box 4</p>
          </div>
        </div>
        {/* Add more slides as needed */}
      </Slider>
      <button className="nav-button prev" onClick={slideLeft}>
        Prev
      </button>
      <button className="nav-button next" onClick={slideRight}>
        Next
      </button>
    </div>
  );
}
